package com.chalapathi.problemsolving;

import java.math.BigInteger;

public class NumericConverter {
    public static BigInteger convertStringToBigInteger(String numberStr) {
        try {
            return new BigInteger(numberStr);
        } catch (NumberFormatException e) {
            throw new IllegalArgumentException("Invalid format for a BigInteger: " + numberStr, e);
        }
    }

    public static void main(String[] args) {
        String largeNumber = "";
        try {
            BigInteger bigInt = convertStringToBigInteger(largeNumber);
            System.out.println("Converted BigInteger: " + bigInt);
        } catch (IllegalArgumentException e) {
            System.err.println(e.getMessage());
        }
        catch (OutOfMemoryError e) {
            System.err.println(e.getMessage());
        }
    }
}
